  return (
    <div className="main-interface">
      {/* È°∂ÈÉ®ÂØºËà™Ê†è */}
      <header className="top-navbar">
        <div className="user-info">
          <div className="avatar" onClick={() => navigate('/settings')}>
            {getAvatarDisplay()}
          </div>
          <div className="user-details">
            <div className="nickname">{userData.nickname}</div>
            <div className="grade-class">{userData.grade} {userData.class}</div>
            <div className="score">ÁßØÂàÜ: {userData.score}</div>
          </div>
        </div>
        <button className="exit-btn" onClick={handleExit}>
          ÈÄÄÂá∫
        </button>
      </header>

      {/* ‰∏ªÂÜÖÂÆπÂå∫Âüü */}
      <main className="main-content">
        <div className="welcome-section">
          <h1>Ê¨¢ËøéÂõûÊù•Ôºå{userData.nickname}ÔºÅ</h1>
          <p>‰ªäÂ§©‰πüË¶ÅÂä†Ê≤πÂì¶ÔΩû</p>
