          <div className="nav-icon">‚öôÔ∏è</div>
          <span>ËÆæÁΩÆ</span>
        </div>
        <div className="nav-item" onClick={handleExit}>
          <div className="nav-icon">üö™</div>
          <span>ÈÄÄÂá∫</span>
        </div>
      </nav>
    </div>
  );
};

export default MainInterface;
