import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './FindPartnerInterface.css';

const FindPartnerInterface = ({ userData }) => {
  const navigate = useNavigate();
  const [selectedRoom, setSelectedRoom] = useState(null);
  const [messages, setMessages] = useState({});
  const [newMessage, setNewMessage] = useState('');
  const [selectedFiles, setSelectedFiles] = useState([]);
  const messagesEndRef = useRef(null);

  const chatRooms = [
    { id: 'basketball', name: 'ÁØÆÁêÉ', icon: 'üèÄ', description: 'ÁØÆÁêÉÁà±Â•ΩËÄÖËÅöÈõÜÂú∞' },
    { id: 'skateboard', name: 'ÊªëÊùø', icon: 'üõπ', description: 'ÊªëÊùøÊäÄÂ∑ß‰∫§ÊµÅ' },
    { id: 'music', name: 'Èü≥‰πê', icon: 'üéµ', description: 'Èü≥‰πêÂàÜ‰∫´‰∏éËÆ®ËÆ∫' },
    { id: 'study', name: 'Â≠¶‰π†', icon: 'üìö', description: 'Â≠¶‰π†‰∫§ÊµÅ‰∫íÂä©' },
    { id: 'gaming', name: 'Ê∏∏Êàè', icon: 'üéÆ', description: 'Ê∏∏ÊàèÊê≠Â≠êÂØªÊâæ' },
    { id: 'photography', name: 'ÊëÑÂΩ±', icon: 'üì∑', description: 'ÊëÑÂΩ±ÊäÄÂ∑ßÂàÜ‰∫´' }
  ];

  useEffect(() => {
    // ‰ªélocalStorageÂä†ËΩΩËÅäÂ§©ËÆ∞ÂΩï
    const savedMessages = localStorage.getItem('chatMessages');
    if (savedMessages) {
      setMessages(JSON.parse(savedMessages));
    }
  }, []);

  useEffect(() => {
    // ÊªöÂä®Âà∞ÊúÄÊñ∞Ê∂àÊÅØ
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, selectedRoom]);

  const saveMessages = (newMessages) => {
    setMessages(newMessages);
    localStorage.setItem('chatMessages', JSON.stringify(newMessages));
  };

  const sendMessage = () => {
    if ((!newMessage.trim() && selectedFiles.length === 0) || !selectedRoom) return;

    const message = {
      id: Date.now(),
      content: newMessage.trim(),
      files: selectedFiles.map(file => ({
        name: file.name,
        type: file.type,
        url: URL.createObjectURL(file)
      })),
      author: {
        nickname: userData.nickname,
        grade: userData.grade,
        class: userData.class,
        avatar: userData.avatar
      },
      timestamp: new Date().toISOString()
    };

    const updatedMessages = {
      ...messages,
      [selectedRoom]: [...(messages[selectedRoom] || []), message]
    };

    saveMessages(updatedMessages);
    setNewMessage('');
    setSelectedFiles([]);
  };

  const handleFileSelect = (e) => {
    const files = Array.from(e.target.files);
    setSelectedFiles(files);
  };

  const formatTimestamp = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString('zh-CN', { 
