  const [selectedPost, setSelectedPost] = useState(null);
  const [commentContent, setCommentContent] = useState('');

  const tabs = ['ÂÄæËØâ‰∏Ä‰∏ã', 'ÂêêÊßΩ‰∏Ä‰∏ã', 'ÂãáÊï¢Ë°®ÁôΩ', '‰∏ÄÁû¨ÁæéÊôØ'];

  useEffect(() => {
    // ‰ªélocalStorageÂä†ËΩΩÂ∏ñÂ≠êÊï∞ÊçÆ
    const savedPosts = localStorage.getItem('listenToWindPosts');
    if (savedPosts) {
      setPosts(JSON.parse(savedPosts));
    }
  }, []);

  const savePosts = (newPosts) => {
    setPosts(newPosts);
    localStorage.setItem('listenToWindPosts', JSON.stringify(newPosts));
  };

  const handleFileSelect = (e) => {
    const files = Array.from(e.target.files);
    setSelectedFiles(files);
  };

  const submitPost = () => {
    if (!postContent.trim() && selectedFiles.length === 0) return;

    const newPost = {
      id: Date.now(),
      content: postContent.trim(),
      files: selectedFiles.map(file => ({
        name: file.name,
        type: file.type,
        url: URL.createObjectURL(file)
      })),
      author: isAnonymous ? {
        nickname: `${userData.grade}Âπ¥Á∫ß ${getGenderDisplay()}`,
        isAnonymous: true
      } : {
        nickname: userData.nickname,
        grade: userData.grade,
        class: userData.class,
        name: userData.name,
        isAnonymous: false
      },
      timestamp: new Date().toISOString(),
      comments: []
    };

    const updatedPosts = {
      ...posts,
      [activeTab]: [newPost, ...posts[activeTab]]
    };

    savePosts(updatedPosts);
    setPostContent('');
    setSelectedFiles([]);
    setShowPostModal(false);
    
    // ÊòæÁ§∫ÊàêÂäüÊèêÁ§∫
    setShowSuccess(true);
    setTimeout(() => setShowSuccess(false), 1000);
  };

  const getGenderDisplay = () => {
    // ËøôÈáåÂèØ‰ª•Ê†πÊçÆÁî®Êà∑ËÆæÁΩÆËøîÂõûÊÄßÂà´ÔºåÊöÇÊó∂ËøîÂõûÈªòËÆ§ÂÄº
    return 'Áî∑';
  };

  const openCommentModal = (post) => {
    setSelectedPost(post);
    setShowCommentModal(true);
  };

  const submitComment = () => {
    if (!commentContent.trim()) return;

    const newComment = {
      id: Date.now(),
      content: commentContent.trim(),
      author: {
        nickname: userData.nickname,
        grade: userData.grade,
        class: userData.class
      },
      timestamp: new Date().toISOString()
    };

    const updatedPosts = { ...posts };
    const postIndex = updatedPosts[activeTab].findIndex(p => p.id === selectedPost.id);
    if (postIndex !== -1) {
      updatedPosts[activeTab][postIndex].comments.push(newComment);
      savePosts(updatedPosts);
    }

    setCommentContent('');
    setShowCommentModal(false);
  };

  const formatTimestamp = (timestamp) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diff = now - date;
    
    if (diff < 60000) return 'ÂàöÂàö';
    if (diff < 3600000) return `${Math.floor(diff / 60000)}ÂàÜÈíüÂâç`;
    if (diff < 86400000) return `${Math.floor(diff / 3600000)}Â∞èÊó∂Ââç`;
    return `${Math.floor(diff / 86400000)}Â§©Ââç`;
  };

  const getTabIcon = (tab) => {
    const icons = {
      'ÂÄæËØâ‰∏Ä‰∏ã': 'üí≠',
      'ÂêêÊßΩ‰∏Ä‰∏ã': 'üò§',
      'ÂãáÊï¢Ë°®ÁôΩ': 'üíï',
      '‰∏ÄÁû¨ÁæéÊôØ': 'üì∏'
    };
    return icons[tab];
  };

  return (
    <div className="listen-to-wind-interface">
      {/* Â§¥ÈÉ® */}
      <header className="wind-header">
        <button className="back-btn" onClick={() => navigate('/')}>
          ‚Üê ËøîÂõû
        </button>
        <h1>Âê¨È£éËØ¥Èõ®</h1>
        <button className="post-btn" onClick={() => setShowPostModal(true)}>
          + ÂèëÂ∏É
        </button>
      </header>

      {/* Ê†áÁ≠æÈ°µ */}
      <nav className="tab-nav">
        {tabs.map(tab => (
          <button
            key={tab}
            className={`tab ${activeTab === tab ? 'active' : ''}`}
            onClick={() => setActiveTab(tab)}
          >
            <span className="tab-icon">{getTabIcon(tab)}</span>
            <span className="tab-text">{tab}</span>
          </button>
        ))}
      </nav>

      {/* Â∏ñÂ≠êÂàóË°® */}
      <main className="posts-content">
        {posts[activeTab].length > 0 ? (
          <div className="posts-list">
            {posts[activeTab].map(post => (
              <div key={post.id} className="post-item">
                <div className="post-header">
                  <div className="author-info">
                    <div className="author-avatar">
                      {post.author.nickname.charAt(0)}
                    </div>
                    <div className="author-details">
                      <div className="author-name">{post.author.nickname}</div>
                      {!post.author.isAnonymous && (
                        <div className="author-meta">
                          {post.author.grade} {post.author.class}
                        </div>
                      )}
                    </div>
                  </div>
                  <div className="post-time">
                    {formatTimestamp(post.timestamp)}
                  </div>
                </div>
                
                {post.content && (
                  <div className="post-content">
                    {post.content}
                  </div>
                )}
                
                {post.files.length > 0 && (
                  <div className="post-files">
                    {post.files.map((file, index) => (
                      <div key={index} className="file-item">
                        {file.type.startsWith('image/') ? (
                          <img src={file.url} alt={file.name} className="post-image" />
                        ) : file.type.startsWith('video/') ? (
                          <video src={file.url} controls className="post-video" />
                        ) : (
                          <div className="file-placeholder">
                            üìé {file.name}
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                )}
                
                <div className="post-actions">
                  <button 
                    className="comment-btn"
                    onClick={() => openCommentModal(post)}
                  >
                    üí¨ ÁïôË®Ä ({post.comments.length})
                  </button>
                </div>
                
                {post.comments.length > 0 && (
                  <div className="comments-section">
                    {post.comments.slice(0, 3).map(comment => (
                      <div key={comment.id} className="comment-item">
                        <div className="comment-author">
                          {comment.author.nickname}
                        </div>
                        <div className="comment-content">
                          {comment.content}
                        </div>
                        <div className="comment-time">
                          {formatTimestamp(comment.timestamp)}
                        </div>
                      </div>
                    ))}
                    {post.comments.length > 3 && (
                      <button 
                        className="view-more-comments"
                        onClick={() => openCommentModal(post)}
                      >
                        Êü•ÁúãÂÖ®ÈÉ® {post.comments.length} Êù°ÁïôË®Ä
                      </button>
                    )}
                  </div>
                )}
              </div>
            ))}
          </div>
        ) : (
          <div className="empty-state">
            <div className="empty-icon">{getTabIcon(activeTab)}</div>
            <h3>ÊöÇÊó†ÂÜÖÂÆπ</h3>
            <p>Êàê‰∏∫Á¨¨‰∏Ä‰∏™Âú®"{activeTab}"ÁâàÂùóÂèëÂ∏ÉÂÜÖÂÆπÁöÑ‰∫∫ÂêßÔºÅ</p>
          </div>
        )}
      </main>

      {/* ÂèëÂ∏ÉÂ∏ñÂ≠êÊ®°ÊÄÅÊ°Ü */}
      {showPostModal && (
        <div className="modal-overlay">
          <div className="modal post-modal">
            <h3>ÂèëÂ∏ÉÂà∞ "{activeTab}"</h3>
            
            <textarea
              className="textarea"
              value={postContent}
              onChange={(e) => setPostContent(e.target.value)}
              placeholder={`Âú®${activeTab}ÂàÜ‰∫´‰Ω†ÁöÑÊÉ≥Ê≥ï...`}
            />
            
            <div className="file-upload">
              <label className="upload-btn">
                üìé Ê∑ªÂä†ÂõæÁâá/ËßÜÈ¢ë
                <input
                  type="file"
                  multiple
                  accept="image/*,video/*"
                  onChange={handleFileSelect}
                  style={{ display: 'none' }}
                />
              </label>
              {selectedFiles.length > 0 && (
                <div className="selected-files">
                  {selectedFiles.map((file, index) => (
                    <div key={index} className="selected-file">
                      {file.name}
                    </div>
                  ))}
                </div>
              )}
            </div>
            
            <div className="anonymous-option">
              <label>
                <input
                  type="checkbox"
                  checked={isAnonymous}
                  onChange={(e) => setIsAnonymous(e.target.checked)}
                />
                ÂåøÂêçÂèëÂ∏ÉÔºàÂè™ÊòæÁ§∫Âπ¥Á∫ßÂíåÊÄßÂà´Ôºâ
              </label>
            </div>
            
            <div className="modal-actions">
              <button 
                className="btn cancel-btn" 
                onClick={() => {
                  setShowPostModal(false);
                  setPostContent('');
                  setSelectedFiles([]);
                  setIsAnonymous(false);
                }}
              >
                ÂèñÊ∂à
              </button>
              <button 
                className="btn submit-btn" 
                onClick={submitPost}
                disabled={!postContent.trim() && selectedFiles.length === 0}
              >
                ÂèëÂ∏É
              </button>
            </div>
          </div>
        </div>
      )}

      {/* ÁïôË®ÄÊ®°ÊÄÅÊ°Ü */}
      {showCommentModal && selectedPost && (
        <div className="modal-overlay">
          <div className="modal comment-modal">
            <h3>ÁïôË®Ä</h3>
            
            <div className="original-post">
              <div className="post-author">{selectedPost.author.nickname}</div>
              <div className="post-content">{selectedPost.content}</div>
            </div>
            
            <div className="comments-list">
              {selectedPost.comments.map(comment => (
                <div key={comment.id} className="comment-item">
                  <div className="comment-author">{comment.author.nickname}</div>
                  <div className="comment-content">{comment.content}</div>
                  <div className="comment-time">{formatTimestamp(comment.timestamp)}</div>
                </div>
              ))}
            </div>
            
            <textarea
              className="textarea"
              value={commentContent}
              onChange={(e) => setCommentContent(e.target.value)}
              placeholder="ÂÜô‰∏ã‰Ω†ÁöÑÁïôË®Ä..."
            />
            
            <div className="modal-actions">
              <button 
                className="btn cancel-btn" 
                onClick={() => {
                  setShowCommentModal(false);
                  setCommentContent('');
                  setSelectedPost(null);
                }}
              >
                ÂèñÊ∂à
              </button>
              <button 
                className="btn submit-btn" 
                onClick={submitComment}
                disabled={!commentContent.trim()}
              >
                ÂèëÈÄÅ
              </button>
            </div>
          </div>
        </div>
      )}

      {/* ÊàêÂäüÊèêÁ§∫ */}
      {showSuccess && (
        <div className="success-message">
          Êî∂Âà∞Âï¶ÔºÅ
        </div>
      )}
    </div>
  );
};

export default ListenToWindInterface;
