import React, { useState } from 'react';
import './LoginRegister.css';

const LoginRegister = ({ onLogin }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [registerType, setRegisterType] = useState('email'); // 'email' or 'phone'
  const [formData, setFormData] = useState({
    email: '',
    phone: '',
    password: '',
    confirmPassword: '',
    nickname: '',
    verificationCode: ''
  });
  const [errors, setErrors] = useState({});
  const [isCodeSent, setIsCodeSent] = useState(false);
  const [countdown, setCountdown] = useState(0);

  // Ë°®ÂçïÈ™åËØÅ
  const validateForm = () => {
    const newErrors = {};

    if (isLogin) {
      // ÁôªÂΩïÈ™åËØÅ
      if (registerType === 'email' && !formData.email) {
        newErrors.email = 'ËØ∑ËæìÂÖ•ÈÇÆÁÆ±';
      } else if (registerType === 'email' && !/\S+@\S+\.\S+/.test(formData.email)) {
        newErrors.email = 'ÈÇÆÁÆ±Ê†ºÂºè‰∏çÊ≠£Á°Æ';
      }

      if (registerType === 'phone' && !formData.phone) {
        newErrors.phone = 'ËØ∑ËæìÂÖ•ÊâãÊú∫Âè∑';
      } else if (registerType === 'phone' && !/^1[3-9]\d{9}$/.test(formData.phone)) {
        newErrors.phone = 'ÊâãÊú∫Âè∑Ê†ºÂºè‰∏çÊ≠£Á°Æ';
      }

      if (!formData.password) {
        newErrors.password = 'ËØ∑ËæìÂÖ•ÂØÜÁ†Å';
      }
    } else {
      // Ê≥®ÂÜåÈ™åËØÅ
      if (registerType === 'email' && !formData.email) {
        newErrors.email = 'ËØ∑ËæìÂÖ•ÈÇÆÁÆ±';
      } else if (registerType === 'email' && !/\S+@\S+\.\S+/.test(formData.email)) {
        newErrors.email = 'ÈÇÆÁÆ±Ê†ºÂºè‰∏çÊ≠£Á°Æ';
      }

      if (registerType === 'phone' && !formData.phone) {
        newErrors.phone = 'ËØ∑ËæìÂÖ•ÊâãÊú∫Âè∑';
      } else if (registerType === 'phone' && !/^1[3-9]\d{9}$/.test(formData.phone)) {
        newErrors.phone = 'ÊâãÊú∫Âè∑Ê†ºÂºè‰∏çÊ≠£Á°Æ';
      }

      if (!formData.password) {
        newErrors.password = 'ËØ∑ËæìÂÖ•ÂØÜÁ†Å';
      } else if (formData.password.length < 6) {
        newErrors.password = 'ÂØÜÁ†ÅÈïøÂ∫¶Ëá≥Â∞ë6‰Ωç';
      }

      if (!formData.confirmPassword) {
        newErrors.confirmPassword = 'ËØ∑Á°ÆËÆ§ÂØÜÁ†Å';
      } else if (formData.password !== formData.confirmPassword) {
        newErrors.confirmPassword = '‰∏§Ê¨°ÂØÜÁ†ÅËæìÂÖ•‰∏ç‰∏ÄËá¥';
      }

      if (!formData.nickname) {
        newErrors.nickname = 'ËØ∑ËæìÂÖ•ÊòµÁß∞';
      }

      if (!formData.verificationCode) {
        newErrors.verificationCode = 'ËØ∑ËæìÂÖ•È™åËØÅÁ†Å';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // ÂèëÈÄÅÈ™åËØÅÁ†Å
  const sendVerificationCode = () => {
    if (registerType === 'email' && !formData.email) {
      setErrors({ email: 'ËØ∑ÂÖàËæìÂÖ•ÈÇÆÁÆ±' });
      return;
    }
    if (registerType === 'phone' && !formData.phone) {
      setErrors({ phone: 'ËØ∑ÂÖàËæìÂÖ•ÊâãÊú∫Âè∑' });
      return;
    }

    // Ê®°ÊãüÂèëÈÄÅÈ™åËØÅÁ†Å
    setIsCodeSent(true);
    setCountdown(60);

    const timer = setInterval(() => {
      setCountdown(prev => {
        if (prev <= 1) {
          clearInterval(timer);
          setIsCodeSent(false);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    // Ê®°ÊãüÈ™åËØÅÁ†ÅÔºàÂÆûÈôÖÈ°πÁõÆ‰∏≠Â∫îËØ•ÊòØÂêéÁ´ØÂèëÈÄÅÔºâ
    const mockCode = '123456';
    setTimeout(() => {
      alert(`È™åËØÅÁ†ÅÂ∑≤ÂèëÈÄÅ${registerType === 'email' ? 'Âà∞ÈÇÆÁÆ±' : 'Âà∞ÊâãÊú∫'}Ôºö${mockCode}`);
    }, 1000);
  };

  // Êèê‰∫§Ë°®Âçï
  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    if (isLogin) {
      // ÁôªÂΩïÈÄªËæë
      const userData = {
        email: registerType === 'email' ? formData.email : '',
        phone: registerType === 'phone' ? formData.phone : '',
        nickname: formData.nickname || 'Áî®Êà∑',
      };
      
      onLogin(userData);
    } else {
      // Ê≥®ÂÜåÈÄªËæë
      if (formData.verificationCode !== '123456') {
        setErrors({ verificationCode: 'È™åËØÅÁ†ÅÈîôËØØ' });
        return;
      }

      const userData = {
        email: registerType === 'email' ? formData.email : '',
        phone: registerType === 'phone' ? formData.phone : '',
        nickname: formData.nickname,
      };

      // Ê≥®ÂÜåÊàêÂäüÂêéÁõ¥Êé•ÁôªÂΩï
      onLogin(userData);
    }
  };

  // ËæìÂÖ•Ê°ÜÂèòÂåñÂ§ÑÁêÜ
  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    // Ê∏ÖÈô§ÂØπÂ∫îÂ≠óÊÆµÁöÑÈîôËØØ
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  return (
    <div className="login-register-container">
      <div className="form-wrapper">
        <div className="form-header">
          <h1 className="app-title">üåü Âä±ÂøóËΩØ‰ª∂</h1>
          <p className="app-subtitle">ËÆ©ÊØè‰∏ÄÂ§©ÈÉΩÂÖÖÊª°Ê≠£ËÉΩÈáè</p>
        </div>

        <div className="tab-switcher">
          <button 
            className={`tab-btn ${isLogin ? 'active' : ''}`}
            onClick={() => setIsLogin(true)}
          >
            ÁôªÂΩï
          </button>
          <button 
            className={`tab-btn ${!isLogin ? 'active' : ''}`}
            onClick={() => setIsLogin(false)}
          >
            Ê≥®ÂÜå
          </button>
        </div>

        <form onSubmit={handleSubmit} className="auth-form">
          {/* Ê≥®ÂÜåÊñπÂºèÈÄâÊã© */}
          <div className="register-type-switcher">
            <button
              type="button"
              className={`type-btn ${registerType === 'email' ? 'active' : ''}`}
              onClick={() => setRegisterType('email')}
            >
              üìß ÈÇÆÁÆ±{isLogin ? 'ÁôªÂΩï' : 'Ê≥®ÂÜå'}
            </button>
            <button
              type="button"
              className={`type-btn ${registerType === 'phone' ? 'active' : ''}`}
              onClick={() => setRegisterType('phone')}
            >
              üì± ÊâãÊú∫{isLogin ? 'ÁôªÂΩï' : 'Ê≥®ÂÜå'}
            </button>
          </div>

          {/* ÈÇÆÁÆ±ËæìÂÖ• */}
          {registerType === 'email' && (
            <div className="form-group">
              <input
                type="email"
                placeholder="ËØ∑ËæìÂÖ•ÈÇÆÁÆ±Âú∞ÂùÄ"
                value={formData.email}
                onChange={(e) => handleInputChange('email', e.target.value)}
                className={`form-input ${errors.email ? 'error' : ''}`}
              />
              {errors.email && <span className="error-text">{errors.email}</span>}
            </div>
          )}

          {/* ÊâãÊú∫ËæìÂÖ• */}
          {registerType === 'phone' && (
            <div className="form-group">
              <input
                type="tel"
                placeholder="ËØ∑ËæìÂÖ•ÊâãÊú∫Âè∑"
                value={formData.phone}
                onChange={(e) => handleInputChange('phone', e.target.value)}
                className={`form-input ${errors.phone ? 'error' : ''}`}
              />
              {errors.phone && <span className="error-text">{errors.phone}</span>}
            </div>
          )}

          {/* ÂØÜÁ†ÅËæìÂÖ• */}
          <div className="form-group">
            <input
              type="password"
              placeholder="ËØ∑ËæìÂÖ•ÂØÜÁ†Å"
              value={formData.password}
              onChange={(e) => handleInputChange('password', e.target.value)}
              className={`form-input ${errors.password ? 'error' : ''}`}
            />
            {errors.password && <span className="error-text">{errors.password}</span>}
          </div>

          {/* Ê≥®ÂÜåÊó∂ÁöÑÈ¢ùÂ§ñÂ≠óÊÆµ */}
          {!isLogin && (
            <>
              <div className="form-group">
                <input
                  type="password"
                  placeholder="ËØ∑Á°ÆËÆ§ÂØÜÁ†Å"
                  value={formData.confirmPassword}
                  onChange={(e) => handleInputChange('confirmPassword', e.target.value)}
                  className={`form-input ${errors.confirmPassword ? 'error' : ''}`}
                />
                {errors.confirmPassword && <span className="error-text">{errors.confirmPassword}</span>}
              </div>

              <div className="form-group">
                <input
                  type="text"
                  placeholder="ËØ∑ËæìÂÖ•ÊòµÁß∞"
                  value={formData.nickname}
                  onChange={(e) => handleInputChange('nickname', e.target.value)}
                  className={`form-input ${errors.nickname ? 'error' : ''}`}
                />
                {errors.nickname && <span className="error-text">{errors.nickname}</span>}
              </div>

              <div className="form-group verification-group">
                <input
                  type="text"
                  placeholder="ËØ∑ËæìÂÖ•È™åËØÅÁ†Å"
                  value={formData.verificationCode}
                  onChange={(e) => handleInputChange('verificationCode', e.target.value)}
                  className={`form-input verification-input ${errors.verificationCode ? 'error' : ''}`}
                />
                <button
                  type="button"
                  className="verification-btn"
                  onClick={sendVerificationCode}
                  disabled={isCodeSent}
                >
                  {isCodeSent ? `${countdown}s` : 'Ëé∑ÂèñÈ™åËØÅÁ†Å'}
                </button>
                {errors.verificationCode && <span className="error-text">{errors.verificationCode}</span>}
              </div>
            </>
          )}

          <button type="submit" className="submit-btn">
            {isLogin ? 'Á´ãÂç≥ÁôªÂΩï' : 'Ê≥®ÂÜåÂπ∂ÁôªÂΩï'}
          </button>

          {isLogin && (
            <div className="login-options">
              <button type="button" className="forgot-password">
                ÂøòËÆ∞ÂØÜÁ†ÅÔºü
              </button>
            </div>
          )}
        </form>

        <div className="form-footer">
          <p>ÁªßÁª≠‰ΩøÁî®Âç≥Ë°®Á§∫ÊÇ®ÂêåÊÑèÊàë‰ª¨ÁöÑ<span className="link">ÊúçÂä°Êù°Ê¨æ</span>Âíå<span className="link">ÈöêÁßÅÊîøÁ≠ñ</span></p>
        </div>
      </div>
    </div>
  );
};

export default LoginRegister;
