import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './SettingsInterface.css';

const SettingsInterface = ({ userData, updateUserData }) => {
  const navigate = useNavigate();
  const [editingField, setEditingField] = useState(null);
  const [tempValues, setTempValues] = useState({});
  const [showSuccess, setShowSuccess] = useState('');

  const settingsFields = [
    { key: 'avatar', label: 'Â§¥ÂÉè', type: 'file', points: 10 },
    { key: 'nickname', label: 'ÊòµÁß∞', type: 'text', points: 10 },
    { key: 'grade', label: 'Âπ¥Á∫ß', type: 'select', points: 10, options: ['È´ò‰∏Ä', 'È´ò‰∫å', 'È´ò‰∏â'] },
    { key: 'class', label: 'Áè≠Á∫ß', type: 'text', points: 10 },
    { key: 'name', label: 'ÂßìÂêç', type: 'text', points: 10 },
    { key: 'isAnonymous', label: 'ÈªòËÆ§ÂåøÂêçÂèëÂ∏É', type: 'checkbox', points: 0 }
  ];

  const startEdit = (field) => {
    setEditingField(field.key);
    setTempValues({ [field.key]: userData[field.key] || '' });
  };

  const cancelEdit = () => {
    setEditingField(null);
    setTempValues({});
  };

  const saveEdit = (field) => {
    const newValue = tempValues[field.key];
    const oldValue = userData[field.key];
    
    // Ê£ÄÊü•ÊòØÂê¶ÊúâÂÆûÈôÖÊõ¥Êîπ
    if (newValue !== oldValue) {
      // Â¶ÇÊûúÊòØÊñ∞ÁöÑËÆæÁΩÆ‰∏îÊúâÁßØÂàÜÂ•ñÂä±
      if (field.points > 0 && (!oldValue || oldValue === '')) {
        updateUserData({
          [field.key]: newValue,
          score: userData.score + field.points
        });
        showSuccessMessage(`ËÆæÁΩÆÊàêÂäüÔºÅËé∑Âæó ${field.points} ÁßØÂàÜÂ•ñÂä±ÔºÅ`);
      } else {
        updateUserData({ [field.key]: newValue });
        showSuccessMessage('ËÆæÁΩÆÂ∑≤‰øùÂ≠òÔºÅ');
      }
    }
    
    setEditingField(null);
    setTempValues({});
  };

  const handleFileChange = (e, field) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        const newValue = event.target.result;
        const oldValue = userData[field.key];
        
        if (field.points > 0 && (!oldValue || oldValue === '')) {
          updateUserData({
            [field.key]: newValue,
            score: userData.score + field.points
          });
          showSuccessMessage(`Â§¥ÂÉèËÆæÁΩÆÊàêÂäüÔºÅËé∑Âæó ${field.points} ÁßØÂàÜÂ•ñÂä±ÔºÅ`);
        } else {
          updateUserData({ [field.key]: newValue });
          showSuccessMessage('Â§¥ÂÉèÂ∑≤Êõ¥Êñ∞ÔºÅ');
        }
      };
      reader.readAsDataURL(file);
    }
  };

  const showSuccessMessage = (message) => {
    setShowSuccess(message);
    setTimeout(() => setShowSuccess(''), 2000);
  };

  const getFieldValue = (field) => {
    if (editingField === field.key) {
      return tempValues[field.key];
    }
    return userData[field.key] || '';
  };

  const getFieldDisplay = (field) => {
    const value = userData[field.key];
    
    if (field.key === 'avatar') {
      if (value) {
        return <img src={value} alt="Â§¥ÂÉè" className="avatar-preview" />;
      }
      return <div className="avatar-placeholder">Êú™ËÆæÁΩÆÂ§¥ÂÉè</div>;
    }
    
    if (field.key === 'isAnonymous') {
      return value ? 'ÂºÄÂêØ' : 'ÂÖ≥Èó≠';
    }
    
    return value || 'Êú™ËÆæÁΩÆ';
  };

  const hasValue = (field) => {
    const value = userData[field.key];
    return value && value !== '';
  };

  const getTotalCompletedSettings = () => {
    return settingsFields.filter(field => 
      field.points > 0 && hasValue(field)
    ).length;
  };

  const getTotalPossiblePoints = () => {
    return settingsFields.reduce((total, field) => total + field.points, 0);
  };

  return (
    <div className="settings-interface">
      {/* Â§¥ÈÉ® */}
      <header className="settings-header">
        <button className="back-btn" onClick={() => navigate('/')}>
          ‚Üê ËøîÂõû
        </button>
        <h1>‰∏™‰∫∫ËÆæÁΩÆ</h1>
        <div className="completion-status">
          {getTotalCompletedSettings()}/{settingsFields.filter(f => f.points > 0).length}
        </div>
      </header>

      {/* ÁßØÂàÜÂ±ïÁ§∫ */}
      <div className="score-section">
        <div className="score-card">
          <div className="score-icon">üèÜ</div>
          <div className="score-info">
            <div className="score-number">{userData.score}</div>
            <div className="score-label">ÊÄªÁßØÂàÜ</div>
          </div>
          <div className="score-progress">
            <div className="progress-text">
              ÂÆåÂñÑÂ∫¶: {Math.round((userData.score / getTotalPossiblePoints()) * 100)}%
            </div>
            <div className="progress-bar">
              <div 
                className="progress-fill"
                style={{ width: `${(userData.score / getTotalPossiblePoints()) * 100}%` }}
              ></div>
            </div>
          </div>
        </div>
      </div>

      {/* ËÆæÁΩÆÈ°πÂàóË°® */}
      <main className="settings-content">
        <div className="settings-list">
          {settingsFields.map(field => (
            <div key={field.key} className="setting-item">
              <div className="setting-header">
                <div className="setting-label">
                  {field.label}
                  {field.points > 0 && !hasValue(field) && (
                    <span className="points-badge">+{field.points}ÂàÜ</span>
                  )}
                  {hasValue(field) && (
                    <span className="completed-badge">‚úì</span>
                  )}
                </div>
                {editingField !== field.key && (
                  <button 
                    className="edit-btn"
                    onClick={() => startEdit(field)}
                  >
                    ÁºñËæë
                  </button>
                )}
              </div>

              <div className="setting-content">
                {editingField === field.key ? (
                  <div className="setting-edit">
                    {field.type === 'text' && (
                      <input
                        type="text"
                        className="input"
                        value={getFieldValue(field)}
                        onChange={(e) => setTempValues({
                          ...tempValues,
                          [field.key]: e.target.value
                        })}
                        placeholder={`ËØ∑ËæìÂÖ•${field.label}`}
                        autoFocus
                      />
                    )}
                    
                    {field.type === 'select' && (
                      <select
                        className="input"
                        value={getFieldValue(field)}
                        onChange={(e) => setTempValues({
                          ...tempValues,
                          [field.key]: e.target.value
                        })}
                      >
                        <option value="">ËØ∑ÈÄâÊã©{field.label}</option>
                        {field.options.map(option => (
                          <option key={option} value={option}>{option}</option>
                        ))}
                      </select>
                    )}
                    
                    {field.type === 'checkbox' && (
                      <label className="checkbox-label">
                        <input
                          type="checkbox"
                          checked={getFieldValue(field)}
                          onChange={(e) => setTempValues({
                            ...tempValues,
                            [field.key]: e.target.checked
                          })}
                        />
                        {getFieldValue(field) ? 'ÂºÄÂêØÂåøÂêçÊ®°Âºè' : 'ÂÖ≥Èó≠ÂåøÂêçÊ®°Âºè'}
                      </label>
                    )}
                    
                    <div className="edit-actions">
                      <button className="btn cancel-btn" onClick={cancelEdit}>
                        ÂèñÊ∂à
                      </button>
                      <button 
                        className="btn save-btn" 
                        onClick={() => saveEdit(field)}
                        disabled={field.type === 'text' && !tempValues[field.key]?.trim()}
                      >
                        ‰øùÂ≠ò
                      </button>
                    </div>
                  </div>
                ) : (
                  <div className="setting-display">
                    {field.type === 'file' ? (
                      <div className="file-setting">
                        {getFieldDisplay(field)}
                        <label className="upload-btn">
                          <input
                            type="file"
                            accept="image/*"
                            onChange={(e) => handleFileChange(e, field)}
                            style={{ display: 'none' }}
                          />
                          {hasValue(field) ? 'Êõ¥Êç¢Â§¥ÂÉè' : '‰∏ä‰º†Â§¥ÂÉè'}
                        </label>
                      </div>
                    ) : (
                      <div className="field-value">
                        {getFieldDisplay(field)}
                      </div>
                    )}
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>

        {/* ÁßØÂàÜËØ¥Êòé */}
        <div className="points-info">
          <h3>üéØ ÁßØÂàÜËé∑ÂèñËØ¥Êòé</h3>
          <ul>
            <li>È¶ñÊ¨°ËÆæÁΩÆÂ§¥ÂÉèÔºö+10ÂàÜ</li>
            <li>È¶ñÊ¨°ËÆæÁΩÆÊòµÁß∞Ôºö+10ÂàÜ</li>
            <li>È¶ñÊ¨°ËÆæÁΩÆÂπ¥Á∫ßÔºö+10ÂàÜ</li>
            <li>È¶ñÊ¨°ËÆæÁΩÆÁè≠Á∫ßÔºö+10ÂàÜ</li>
            <li>È¶ñÊ¨°ËÆæÁΩÆÂßìÂêçÔºö+10ÂàÜ</li>
            <li>ÂÆåÂñÑÊâÄÊúâ‰ø°ÊÅØÂèØËé∑ÂæóÊÄªËÆ°50ÂàÜÔºÅ</li>
          </ul>
        </div>

        {/* ÂÖ∂‰ªñÊìç‰Ωú */}
        <div className="other-actions">
          <button 
            className="action-btn danger-btn"
            onClick={() => {
              if (window.confirm('Á°ÆÂÆöË¶ÅÊ∏ÖÈô§ÊâÄÊúâÊï∞ÊçÆÂêóÔºüÊ≠§Êìç‰Ωú‰∏çÂèØÊÅ¢Â§çÔºÅ')) {
                localStorage.clear();
                updateUserData({
                  avatar: null,
                  nickname: 'Áî®Êà∑',
                  grade: 'È´ò‰∏Ä',
                  class: '1Áè≠',
                  name: 'Âº†‰∏â',
                  score: 0,
                  isAnonymous: false
                });
                showSuccessMessage('Êï∞ÊçÆÂ∑≤Ê∏ÖÈô§ÔºÅ');
              }
            }}
          >
            üóëÔ∏è Ê∏ÖÈô§ÊâÄÊúâÊï∞ÊçÆ
          </button>
        </div>
      </main>

      {/* ÊàêÂäüÊèêÁ§∫ */}
      {showSuccess && (
        <div className="success-message">
          {showSuccess}
        </div>
      )}
    </div>
  );
};

export default SettingsInterface;
